import React, { useState } from 'react';
import { useAppStore } from '../store/useAppStore';
import './Home.css';

const Home: React.FC = () => {
  const { generatePlanFromGoal, generateRecipesFromReceipt, isLoading, error, clearError } = useAppStore();
  const [showReceiptModal, setShowReceiptModal] = useState(false);
  const [showGoalModal, setShowGoalModal] = useState(false);
  const [generatedRecipes, setGeneratedRecipes] = useState<any[]>([]);

  const goals = [
    { id: 'fitness', name: '健身增肌', description: '高蛋白饮食，支持肌肉增长' },
    { id: 'weight-loss', name: '减重瘦身', description: '低卡路里，健康减重' },
    { id: 'muscle-gain', name: '增肌塑形', description: '高蛋白高碳水，快速增肌' },
    { id: 'low-carb', name: '低碳水', description: '控制碳水摄入，稳定血糖' },
    { id: 'vegetarian', name: '素食主义', description: '植物性饮食，环保健康' }
  ];

  const handleGoalSelect = async (goal: string) => {
    try {
      await generatePlanFromGoal(goal);
      setShowGoalModal(false);
    } catch (error) {
      console.error('Error generating plan:', error);
    }
  };

  const handleReceiptUpload = async () => {
    try {
      // Simulate receipt items - in real app this would come from OCR
      const receiptItems = [
        'chicken breast', 'broccoli', 'sweet potatoes', 'olive oil',
        'quinoa', 'bell peppers', 'tomatoes', 'onions'
      ];

      const recipes = await generateRecipesFromReceipt(receiptItems);
      setGeneratedRecipes(recipes);
      setShowReceiptModal(false);
    } catch (error) {
      console.error('Error generating recipes:', error);
    }
  };

  const closeModals = () => {
    setShowReceiptModal(false);
    setShowGoalModal(false);
    setGeneratedRecipes([]);
    clearError();
  };

  return (
    <div className="home">
      <div className="home-header">
        <h1>SmartMeal</h1>
        <p>您的AI智能饮食助手</p>
      </div>

      <div className="home-content">
        <div className="path-section">
          <h2>我需要饮食计划</h2>
          <p>基于您的健康目标，生成个性化的一周饮食计划</p>
          <button 
            className="primary-button"
            onClick={() => setShowGoalModal(true)}
            disabled={isLoading}
          >
            {isLoading ? '生成中...' : '开始规划'}
          </button>
        </div>

        <div className="path-section">
          <h2>我刚买完菜</h2>
          <p>上传购物小票，AI为您推荐可用食材制作的菜谱</p>
          <button 
            className="secondary-button"
            onClick={() => setShowReceiptModal(true)}
            disabled={isLoading}
          >
            {isLoading ? '处理中...' : '上传小票'}
          </button>
        </div>
      </div>

      {/* Goal Selection Modal */}
      {showGoalModal && (
        <div className="modal-overlay" onClick={closeModals}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>选择您的健康目标</h3>
            <div className="goals-grid">
              {goals.map((goal) => (
                <div 
                  key={goal.id} 
                  className="goal-card"
                  onClick={() => handleGoalSelect(goal.id)}
                >
                  <h4>{goal.name}</h4>
                  <p>{goal.description}</p>
                </div>
              ))}
            </div>
            <button className="close-button" onClick={closeModals}>
              取消
            </button>
          </div>
        </div>
      )}

      {/* Receipt Upload Modal */}
      {showReceiptModal && (
        <div className="modal-overlay" onClick={closeModals}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>上传购物小票</h3>
            <div className="receipt-upload">
              <div className="receipt-preview">
                <img 
                  src="/receipt-sample.jpg" 
                  alt="Sample Receipt"
                  onError={(e) => {
                    e.currentTarget.style.display = 'none';
                    e.currentTarget.nextElementSibling!.style.display = 'block';
                  }}
                />
                <div className="receipt-placeholder" style={{ display: 'none' }}>
                  <div className="receipt-text">
                    <h4>购物小票示例</h4>
                    <p>Chicken Breast - $8.99</p>
                    <p>Broccoli - $2.49</p>
                    <p>Sweet Potatoes - $3.99</p>
                    <p>Olive Oil - $5.99</p>
                    <p>Quinoa - $4.99</p>
                    <p>Bell Peppers - $3.49</p>
                    <p>Tomatoes - $2.99</p>
                    <p>Onions - $1.99</p>
                  </div>
                </div>
              </div>
              <button 
                className="primary-button"
                onClick={handleReceiptUpload}
                disabled={isLoading}
              >
                {isLoading ? '分析中...' : '分析小票并生成菜谱'}
              </button>
            </div>
            <button className="close-button" onClick={closeModals}>
              取消
            </button>
          </div>
        </div>
      )}

      {/* Generated Recipes Modal */}
      {generatedRecipes.length > 0 && (
        <div className="modal-overlay" onClick={closeModals}>
          <div className="modal-content recipes-modal" onClick={(e) => e.stopPropagation()}>
            <h3>为您推荐的菜谱</h3>
            <div className="recipes-grid">
              {generatedRecipes.map((recipe, index) => (
                <div key={index} className="recipe-card">
                  <h4>{recipe.name}</h4>
                  <p>{recipe.description}</p>
                  <div className="recipe-details">
                    <span>⏱️ {recipe.prep_time + recipe.cook_time}分钟</span>
                    <span>🔥 {recipe.calories_per_serving}卡路里</span>
                  </div>
                  <div className="recipe-ingredients">
                    <strong>主要食材:</strong>
                    <ul>
                      {recipe.ingredients.slice(0, 3).map((ingredient: string, i: number) => (
                        <li key={i}>{ingredient}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              ))}
            </div>
            <button className="close-button" onClick={closeModals}>
              关闭
            </button>
          </div>
        </div>
      )}

      {error && (
        <div className="error-message">
          {error}
          <button onClick={clearError}>✕</button>
        </div>
      )}
    </div>
  );
};

export default Home;
