import React, { useState, useEffect } from 'react';
import { useAppStore } from '../store/useAppStore';
import RecipeCard from '../components/RecipeCard';
import './MealPlan.css';

const MealPlan: React.FC = () => {
  const { currentPlan, currentShoppingList, isLoading, error, clearError } = useAppStore();
  const [showShoppingList, setShowShoppingList] = useState(false);

  const days = [
    { key: 'monday', name: 'Âë®‰∏Ä' },
    { key: 'tuesday', name: 'Âë®‰∫å' },
    { key: 'wednesday', name: 'Âë®‰∏â' },
    { key: 'thursday', name: 'Âë®Âõõ' },
    { key: 'friday', name: 'Âë®‰∫î' },
    { key: 'saturday', name: 'Âë®ÂÖ≠' },
    { key: 'sunday', name: 'Âë®Êó•' }
  ];

  const meals = [
    { key: 'breakfast', name: 'Êó©È§ê', icon: 'üåÖ' },
    { key: 'lunch', name: 'ÂçàÈ§ê', icon: '‚òÄÔ∏è' },
    { key: 'dinner', name: 'ÊôöÈ§ê', icon: 'üåô' }
  ];

  const getMealRecipe = (dayKey: string, mealKey: string) => {
    if (!currentPlan) return null;
    const day = currentPlan[dayKey as keyof typeof currentPlan];
    if (!day) return null;
    return day[mealKey as keyof typeof day];
  };

  const getDayNutrition = (dayKey: string) => {
    if (!currentPlan) return null;
    const day = currentPlan[dayKey as keyof typeof currentPlan];
    if (!day) return null;
    return {
      calories: day.total_calories,
      protein: day.total_protein,
      carbs: day.total_carbs,
      fat: day.total_fat
    };
  };

  if (isLoading) {
    return (
      <div className="meal-plan">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Ê≠£Âú®ÁîüÊàêÊÇ®ÁöÑ‰∏™ÊÄßÂåñÈ•ÆÈ£üËÆ°Âàí...</p>
        </div>
      </div>
    );
  }

  if (!currentPlan) {
    return (
      <div className="meal-plan">
        <div className="empty-state">
          <h2>ËøòÊ≤°ÊúâÈ•ÆÈ£üËÆ°Âàí</h2>
          <p>ÂâçÂæÄÈ¶ñÈ°µÂàõÂª∫ÊÇ®ÁöÑ‰∏™ÊÄßÂåñÈ•ÆÈ£üËÆ°Âàí</p>
        </div>
      </div>
    );
  }

  return (
    <div className="meal-plan">
      <div className="meal-plan-header">
        <h1>Êú¨Âë®È•ÆÈ£üËÆ°Âàí</h1>
        {currentShoppingList && (
          <button 
            className="shopping-list-button"
            onClick={() => setShowShoppingList(true)}
          >
            üõí Êü•ÁúãË¥≠Áâ©Ê∏ÖÂçï
          </button>
        )}
      </div>

      <div className="meal-plan-grid">
        {/* Header row with meal names */}
        <div className="grid-header">
          <div className="day-header"></div>
          {meals.map(meal => (
            <div key={meal.key} className="meal-header">
              <span className="meal-icon">{meal.icon}</span>
              <span className="meal-name">{meal.name}</span>
            </div>
          ))}
        </div>

        {/* Daily rows */}
        {days.map(day => {
          const dayNutrition = getDayNutrition(day.key);
          return (
            <div key={day.key} className="day-row">
              <div className="day-info">
                <h3>{day.name}</h3>
                {dayNutrition && (
                  <div className="day-nutrition">
                    <span>üî• {dayNutrition.calories}Âç°</span>
                    <span>ü•© {dayNutrition.protein}g</span>
                  </div>
                )}
              </div>

              {meals.map(meal => {
                const recipe = getMealRecipe(day.key, meal.key);
                return (
                  <div key={meal.key} className="meal-cell">
                    {recipe && typeof recipe === 'object' ? (
                      <div className="meal-recipe">
                        <h4>{recipe.name}</h4>
                        <p className="recipe-description">{recipe.description}</p>
                        <div className="recipe-meta">
                          <span>‚è±Ô∏è {recipe.prep_time + recipe.cook_time}ÂàÜÈíü</span>
                          <span>üî• {recipe.calories_per_serving}Âç°</span>
                        </div>
                        <button className="view-recipe-button">
                          Êü•ÁúãÂÅöÊ≥ï
                        </button>
                      </div>
                    ) : (
                      <div className="empty-meal">
                        <span>ÊöÇÊó†ÂÆâÊéí</span>
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          );
        })}
      </div>

      {/* Shopping List Modal */}
      {showShoppingList && currentShoppingList && (
        <div className="modal-overlay" onClick={() => setShowShoppingList(false)}>
          <div className="modal-content shopping-modal" onClick={(e) => e.stopPropagation()}>
            <h3>Ë¥≠Áâ©Ê∏ÖÂçï</h3>
            <div className="shopping-list-content">
              <div className="shopping-summary">
                <p><strong>È¢Ñ‰º∞Ë¥πÁî®:</strong> ${currentShoppingList.estimated_cost.toFixed(2)}</p>
                <p><strong>Êé®ËçêÂïÜÂ∫ó:</strong> {currentShoppingList.store_suggestions.join(', ')}</p>
              </div>

              <div className="shopping-categories">
                {Object.entries(currentShoppingList.categories).map(([category, items]) => (
                  <div key={category} className="shopping-category">
                    <h4>{category}</h4>
                    <ul>
                      {items.map((item, index) => (
                        <li key={index}>{item}</li>
                      ))}
                    </ul>
                  </div>
                ))}
              </div>

              <button className="instacart-button">
                üõí ‰∏ÄÈîÆÂêåÊ≠•Âà∞ Instacart
              </button>
            </div>
            <button className="close-button" onClick={() => setShowShoppingList(false)}>
              ÂÖ≥Èó≠
            </button>
          </div>
        </div>
      )}

      {error && (
        <div className="error-message">
          {error}
          <button onClick={clearError}>‚úï</button>
        </div>
      )}
    </div>
  );
};

export default MealPlan;
